import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Calendar, Clock, User, Search, Filter, Plus } from 'lucide-react';
import AppointmentCard from './AppointmentCard';
import AppointmentModal from './AppointmentModal';
import './Appointments.css';

const Appointments = () => {
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [selectedAppointment, setSelectedAppointment] = useState(null);
  
  // Filters
  const [filters, setFilters] = useState({
    status: '',
    search: '',
    date: '',
    doctorId: '',
    page: 1,
    limit: 10
  });

  const [pagination, setPagination] = useState({
    total: 0,
    page: 1,
    pages: 1
  });

  const statusOptions = [
    { value: '', label: 'All Status' },
    { value: 'Scheduled', label: 'Scheduled' },
    { value: 'Confirmed', label: 'Confirmed' },
    { value: 'In Progress', label: 'In Progress' },
    { value: 'Completed', label: 'Completed' },
    { value: 'Cancelled', label: 'Cancelled' }
  ];

  useEffect(() => {
    fetchAppointments();
  }, [filters]);

  const fetchAppointments = async () => {
    try {
      setLoading(true);
      const queryParams = new URLSearchParams();
      
      Object.keys(filters).forEach(key => {
        if (filters[key]) {
          queryParams.append(key, filters[key]);
        }
      });

      const response = await axios.get(`/api/appointments?${queryParams}`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (response.data.success) {
        setAppointments(response.data.data);
        setPagination(response.data.pagination);
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Error fetching appointments');
      console.error('Fetch appointments error:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleFilterChange = (key, value) => {
    setFilters(prev => ({
      ...prev,
      [key]: value,
      page: 1 // Reset to first page on filter change
    }));
  };

  const handlePageChange = (newPage) => {
    setFilters(prev => ({
      ...prev,
      page: newPage
    }));
  };

  const handleCreateAppointment = () => {
    setSelectedAppointment(null);
    setShowModal(true);
  };

  const handleEditAppointment = (appointment) => {
    setSelectedAppointment(appointment);
    setShowModal(true);
  };

  const handleViewAppointment = async (id) => {
    try {
      const response = await axios.get(`/api/appointments/${id}`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (response.data.success) {
        setSelectedAppointment(response.data.data);
        setShowModal(true);
      }
    } catch (err) {
      console.error('View appointment error:', err);
    }
  };

  const handleCancelAppointment = async (id, reason) => {
    if (!window.confirm('Are you sure you want to cancel this appointment?')) {
      return;
    }

    try {
      const response = await axios.put(
        `/api/appointments/${id}/cancel`,
        { reason },
        {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        }
      );

      if (response.data.success) {
        fetchAppointments();
        alert('Appointment cancelled successfully');
      }
    } catch (err) {
      alert(err.response?.data?.message || 'Error cancelling appointment');
    }
  };

  const handleCheckIn = async (id) => {
    try {
      const response = await axios.put(
        `/api/appointments/${id}/checkin`,
        {},
        {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        }
      );

      if (response.data.success) {
        fetchAppointments();
        alert('Patient checked in successfully');
      }
    } catch (err) {
      alert(err.response?.data?.message || 'Error checking in patient');
    }
  };

  const handleComplete = async (id) => {
    try {
      const response = await axios.put(
        `/api/appointments/${id}/complete`,
        {},
        {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        }
      );

      if (response.data.success) {
        fetchAppointments();
        alert('Appointment completed successfully');
      }
    } catch (err) {
      alert(err.response?.data?.message || 'Error completing appointment');
    }
  };

  const handleModalClose = () => {
    setShowModal(false);
    setSelectedAppointment(null);
    fetchAppointments();
  };

  if (loading && appointments.length === 0) {
    return (
      <div className="appointments-loading">
        <div className="spinner"></div>
        <p>Loading appointments...</p>
      </div>
    );
  }

  return (
    <div className="appointments-container">
      <div className="appointments-header">
        <div className="header-left">
          <h1>Appointments</h1>
          <p className="subtitle">Manage patient appointments and schedules</p>
        </div>
        <button className="btn-primary" onClick={handleCreateAppointment}>
          <Plus size={20} />
          New Appointment
        </button>
      </div>

      <div className="appointments-filters">
        <div className="search-box">
          <Search size={20} />
          <input
            type="text"
            placeholder="Search by patient name or ID..."
            value={filters.search}
            onChange={(e) => handleFilterChange('search', e.target.value)}
          />
        </div>

        <div className="filter-group">
          <div className="filter-item">
            <Calendar size={18} />
            <input
              type="date"
              value={filters.date}
              onChange={(e) => handleFilterChange('date', e.target.value)}
            />
          </div>

          <div className="filter-item">
            <Filter size={18} />
            <select
              value={filters.status}
              onChange={(e) => handleFilterChange('status', e.target.value)}
            >
              {statusOptions.map(option => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
          </div>
        </div>
      </div>

      {error && (
        <div className="error-message">
          <p>{error}</p>
        </div>
      )}

      <div className="appointments-stats">
        <div className="stat-card">
          <div className="stat-icon scheduled">
            <Calendar size={24} />
          </div>
          <div className="stat-info">
            <p className="stat-value">
              {appointments.filter(a => a.status === 'Scheduled').length}
            </p>
            <p className="stat-label">Scheduled</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon confirmed">
            <Clock size={24} />
          </div>
          <div className="stat-info">
            <p className="stat-value">
              {appointments.filter(a => a.status === 'Confirmed').length}
            </p>
            <p className="stat-label">Confirmed</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon in-progress">
            <User size={24} />
          </div>
          <div className="stat-info">
            <p className="stat-value">
              {appointments.filter(a => a.status === 'In Progress').length}
            </p>
            <p className="stat-label">In Progress</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon completed">
            <Calendar size={24} />
          </div>
          <div className="stat-info">
            <p className="stat-value">
              {appointments.filter(a => a.status === 'Completed').length}
            </p>
            <p className="stat-label">Completed</p>
          </div>
        </div>
      </div>

      <div className="appointments-list">
        {appointments.length === 0 ? (
          <div className="no-appointments">
            <Calendar size={48} />
            <h3>No appointments found</h3>
            <p>Try adjusting your filters or create a new appointment</p>
          </div>
        ) : (
          appointments.map(appointment => (
            <AppointmentCard
              key={appointment._id}
              appointment={appointment}
              onView={() => handleViewAppointment(appointment._id)}
              onEdit={() => handleEditAppointment(appointment)}
              onCancel={(reason) => handleCancelAppointment(appointment._id, reason)}
              onCheckIn={() => handleCheckIn(appointment._id)}
              onComplete={() => handleComplete(appointment._id)}
            />
          ))
        )}
      </div>

      {pagination.pages > 1 && (
        <div className="pagination">
          <button
            className="pagination-btn"
            disabled={pagination.page === 1}
            onClick={() => handlePageChange(pagination.page - 1)}
          >
            Previous
          </button>

          <div className="pagination-info">
            Page {pagination.page} of {pagination.pages}
          </div>

          <button
            className="pagination-btn"
            disabled={pagination.page === pagination.pages}
            onClick={() => handlePageChange(pagination.page + 1)}
          >
            Next
          </button>
        </div>
      )}

      {showModal && (
        <AppointmentModal
          appointment={selectedAppointment}
          onClose={handleModalClose}
        />
      )}
    </div>
  );
};

export default Appointments;
