import React, { useState, useEffect } from 'react';
import axios from 'axios';
import AppointmentCard from './AppointmentCard';
import './Appointment.css';

const Appointments = () => {
  const [appointments, setAppointments] = useState([]);
  const [newAppointment, setNewAppointment] = useState({
    patientName: '',
    doctorName: '',
    date: ''
  });
  const [selectedAppointment, setSelectedAppointment] = useState(null);
  const [isEditMode, setIsEditMode] = useState(false);

  useEffect(() => {
    fetchAppointments();
  }, []);

  const fetchAppointments = () => {
    axios.get('http://localhost:5000/appointments')
      .then(response => {
        console.log('Fetched appointments:', response.data);
        setAppointments(response.data);
      })
      .catch(error => console.error('Error fetching appointments:', error));
  };

  const handleAddAppointment = (e) => {
    e.preventDefault();
    console.log('Adding appointment:', newAppointment);
    
    axios.post('http://localhost:5000/appointments/add', newAppointment)
      .then(response => {
        console.log('Appointment added:', response.data);
        setAppointments([...appointments, response.data]);
        setNewAppointment({ patientName: '', doctorName: '', date: '' });
        alert('Appointment added successfully!');
      })
      .catch(error => {
        console.error('Error adding appointment:', error);
        alert('Error adding appointment. Please try again.');
      });
  };

  const handleUpdateAppointment = (id, e) => {
    e.preventDefault();
    console.log('Updating appointment:', id, selectedAppointment);
    
    axios.post(`http://localhost:5000/appointments/update/${id}`, selectedAppointment)
      .then(response => {
        console.log('Appointment updated:', response.data);
        const updateApp = { ...selectedAppointment, _id: id };
        setAppointments(
          appointments.map(appointment =>
            appointment._id === id ? updateApp : appointment
          )
        );
        setSelectedAppointment(null);
        setIsEditMode(false);
        alert('Appointment updated successfully!');
      })
      .catch(error => {
        console.error('Error updating appointment:', error);
        alert('Error updating appointment. Please try again.');
      });
  };

  const handleDeleteAppointment = (id) => {
    if (window.confirm('Are you sure you want to delete this appointment?')) {
      console.log('Deleting appointment:', id);
      
      axios.delete(`http://localhost:5000/appointments/delete/${id}`)
        .then(response => {
          console.log('Appointment deleted:', response.data);
          setAppointments(appointments.filter(appointment => appointment._id !== id));
          alert('Appointment deleted successfully!');
        })
        .catch(error => {
          console.error('Error deleting appointment:', error);
          alert('Error deleting appointment. Please try again.');
        });
    }
  };

  const handleEditAppointment = (appointment) => {
    console.log('Editing appointment:', appointment);
    setSelectedAppointment({
      patientName: appointment.patientName,
      doctorName: appointment.doctorName,
      date: appointment.date.split('T')[0]
    });
    setIsEditMode(true);
  };

  const handleCancelEdit = () => {
    setSelectedAppointment(null);
    setIsEditMode(false);
  };

  return (
    <div className='flex-row' style={{ width: "100%", padding: "20px" }}>
      <div className='flex-column add-form'>
        <h4>{isEditMode ? 'Edit Appointment' : 'Add New Appointment'}</h4>
        <form
          className="appointment-form"
          onSubmit={
            isEditMode
              ? (e) => handleUpdateAppointment(selectedAppointment._id || appointments.find(a => 
                  a.patientName === selectedAppointment.patientName && 
                  a.doctorName === selectedAppointment.doctorName
                )?._id, e)
              : handleAddAppointment
          }
        >
          <label>Patient Name:</label>
          <input
            type="text"
            required
            value={isEditMode ? selectedAppointment?.patientName || '' : newAppointment.patientName}
            onChange={(e) =>
              isEditMode
                ? setSelectedAppointment({ ...selectedAppointment, patientName: e.target.value })
                : setNewAppointment({ ...newAppointment, patientName: e.target.value })
            }
          />

          <label>Doctor Name:</label>
          <input
            type="text"
            required
            value={isEditMode ? selectedAppointment?.doctorName || '' : newAppointment.doctorName}
            onChange={(e) =>
              isEditMode
                ? setSelectedAppointment({ ...selectedAppointment, doctorName: e.target.value })
                : setNewAppointment({ ...newAppointment, doctorName: e.target.value })
            }
          />

          <label>Date:</label>
          <input
            type="date"
            required
            value={isEditMode ? selectedAppointment?.date || '' : newAppointment.date}
            onChange={(e) =>
              isEditMode
                ? setSelectedAppointment({ ...selectedAppointment, date: e.target.value })
                : setNewAppointment({ ...newAppointment, date: e.target.value })
            }
          />

          <button type="submit">
            {isEditMode ? 'Update Appointment' : 'Add Appointment'}
          </button>
          
          {isEditMode && (
            <button 
              type="button" 
              onClick={handleCancelEdit}
              style={{ marginTop: '10px', backgroundColor: '#6c757d' }}
            >
              Cancel
            </button>
          )}
        </form>
      </div>

      <div className='appointments'>
        <h3>Appointments ({appointments.length})</h3>
        <div className="appointment-list">
          {appointments.length === 0 ? (
            <p style={{ color: '#999', marginTop: '20px' }}>No appointments yet. Add one to get started!</p>
          ) : (
            appointments.map(appointment => (
              <AppointmentCard
                key={appointment._id}
                appointment={appointment}
                onEdit={handleEditAppointment}
                onDelete={handleDeleteAppointment}
              />
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default Appointments;
